namespace MainAppSpace;

require_once "Database.php";


$object = new Database("localhost" , "root" , "");

$object->createDatabase("BookStore");

$object->useDatabase("BookStore");

$object->createTable("Authors",
                    "isbn VARCHAR(32),
                    author VARCHAR(64),
                    INDEX (isbn(24)),
	                INDEX (author(32))",
                    "MyISAM");

$object->createTable("Books",
                    "isbn VARCHAR(32) NOT NULL,
                    title VARCHAR(64) NOT NULL COLLATE utf8_general_ci,
                    price FLOAT NOT NULL,
                    year YEAR,
                    pages SMALLINT UNSIGNED,
                    orderCount INT UNSIGNED DEFAULT 0,
                    releaseDate TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                    description TEXT,
                    INDEX (isbn(24)),
                    INDEX (title (32)),
                    PRIMARY KEY(isbn)",
                    "MyISAM");

$object->createTable("BookCategory",
                    "isbn VARCHAR(32),
                    idCategory VARCHAR(16),
	                INDEX (isbn(24)),
	                INDEX (idCategory(16))",
                    "MyISAM");

$object->createTable("CategoryName",
                    "idCategory VARCHAR(16),
                    sName VARCHAR(32),
                    PRIMARY KEY(idCategory),
	                INDEX (sName(16))",
                    "MyISAM");

//TODO: из таблицы Books нужно вынести поле orderCount в отдельную таблицу. Аналогично и для releaseDate
